// Goal of this feature/component: Let the used type text into a box, press Enter/click "Add Todo", and have a new  todo task appear in the main todo list

import React, { useState } from 'react';

const AddTodo = ({ onAdd }) => {
    const [inputValue, setInputValue] = useState(''); // inputValue is the named variable - text curently inside the input. 

    const handleSubmit = (e) => {
        e.preventDefault();
        if (inputValue.trim()) {
            onAdd(inputValue);
            setInputValue('');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="Add a new todo"
            />
            <button type="submit">Add Todo</button>
        </form>
    );
};

export default AddTodo;
