import React from 'react';
import TodoItem from './TodoItem';

const TodoList = ({ todos, removeTodo, toggleTodo, editingId, onEdit, onCancel, onSave }) => (
        <ul>
            {todos.map((todo, index) => (
                <TodoItem 
                    key={todo.id} 
                    todo={todo} 
                    editingId={editingId} // This is which task (by id) is being edited
                    onRemove={() => removeTodo(todo.id)}
                    onToggle={() => toggleTodo(todo.id)}
                    onEdit={() => onEdit(todo.id)} // New addition 
                    onCancel={onCancel}
                    onSave={onSave} // we are just passing the function reference to todoItems as we don't know what the user typed into the input box that is in the todoItems.
                />
            ))}
        </ul>
);

export default TodoList;
